function gB(){return fetch("https://cors-anywhere.herokuapp.com/https://www.goodreads.com/review/list?v=2&id=5988902&shelf=currently-reading&key=kKk9lOiT8yMsc5uneNuw").then(e=>{const t=new TextDecoder,r=new DOMParser,n=e.body.getReader(),o=()=>n.read().then(({done:e,value:c})=>{const u=t.decode(c||new Uint8Array,{stream:!e}),a=r.parseFromString(u,"application/xml").querySelector("book");if(a)return n.cancel('Found the "currently reading" book'),a;if(e)throw Error('Could not find the "currently reading" book');return o()});return o()}).catch(e=>console.log({error:e}))}function setCR(){gB().then(e=>{const t=e.querySelector("author").querySelector("name").textContent,r=e.querySelector("title").textContent;document.querySelector("#currentlyreading").innerHTML=`Currently reading <i>"${r}"</i> by ${t}`})}function setY(){const e=(new Date).getFullYear();document.querySelector("#years").textContent=`2018 - ${e}`}setCR(),setY();
